CREATE TABLE EVENT (ID INTEGER GENERATED BY DEFAULT AS IDENTITY (START WITH 1) PRIMARY KEY NOT NULL, DATA DATE, PLACE INTEGER, PRICE DOUBLE, NAME VARCHAR(50))
CREATE TABLE PLACE (ID INTEGER GENERATED BY DEFAULT AS IDENTITY (START WITH 1) PRIMARY KEY NOT NULL, PLACE VARCHAR(50))
CREATE TABLE SEAT (ID INTEGER GENERATED BY DEFAULT AS IDENTITY (START WITH 1) PRIMARY KEY NOT NULL, PLACE_ID INTEGER,FILA VARCHAR(50),LUGAR INTEGER,SEAT_TYPE VARCHAR(50))
CREATE TABLE TICKET (ID INTEGER GENERATED BY DEFAULT AS IDENTITY (START WITH 1) PRIMARY KEY NOT NULL,PLACE_ID INTEGER,EVENT_ID INTEGER,SEAT_ID INTEGER)
ALTER TABLE EVENT ADD CONSTRAINT FK_EVENT_PLACE FOREIGN KEY (PLACE) REFERENCES PLACE (ID)
ALTER TABLE SEAT ADD CONSTRAINT FK_SEAT_PLACE_ID FOREIGN KEY (PLACE_ID) REFERENCES PLACE (ID)
ALTER TABLE TICKET ADD CONSTRAINT FK_TICKET_PLACE_ID FOREIGN KEY (PLACE_ID) REFERENCES PLACE (ID)
ALTER TABLE TICKET ADD CONSTRAINT FK_TICKET_EVENT_ID FOREIGN KEY (EVENT_ID) REFERENCES EVENT (ID)
ALTER TABLE TICKET ADD CONSTRAINT FK_TICKET_SEAT_ID FOREIGN KEY (SEAT_ID) REFERENCES SEAT(ID)
